import React, { useEffect, useState } from 'react';
import { useSearchParams } from 'react-router-dom';

export default function Rezultate() {
  const [searchParams] = useSearchParams();
  const searchTerm = searchParams.get('termen');
  const [produse, setProduse] = useState([]);

  useEffect(() => {
    if (searchTerm) {
      fetch(`http://localhost:5000/api/products/search/${encodeURIComponent(searchTerm)}`)
        .then(res => res.json())
        .then(data => setProduse(data))
        .catch(err => console.error('Eroare încărcare produse:', err));
    }
  }, [searchTerm]);

  return (
    <div>
      <h2>Rezultate pentru: {searchTerm}</h2>
      <ul>
        {produse.map((produs, index) => (
          <li key={index}>{produs.name} - {produs.price} lei</li>
        ))}
      </ul>
    </div>
  );
}
