import axios from 'axios';
import { db } from './firebase';
import { collection, query, where, orderBy, getDocs, addDoc } from 'firebase/firestore';
import { serverTimestamp } from 'firebase/firestore';
const API_BASE = process.env.REACT_APP_API_URL;

const API_URL = process.env.REACT_APP_API_URL;


export const saveSearch = async (uid, searchTerm) => {
  try {
    await addDoc(collection(db, "searchHistory"), {
      uid,
      term: searchTerm,
      timestamp: serverTimestamp()
    });
  } catch (e) {
    console.error("Eroare la salvarea cﾄブtﾄビii:", e);
  }
};

export const cautaProduse = async (termen) => {
  const res = await fetch(`${API_URL}/products/search/${termen}`);
  if (!res.ok) throw new Error("Eroare la interogarea produselor");
  return await res.json();
};

export const getSearchHistory = async (uid) => {
  const q = query(
    collection(db, "searches"),
    where("uid", "==", uid),
    orderBy("timestamp", "desc")
  );

  const snapshot = await getDocs(q);
  return snapshot.docs.map(doc => ({
    id: doc.id,
    ...doc.data()
  }));
};



export const getAllProduse = async () => {
  const res = await axios.get(`${API_URL}/api/products`);
  return res.data;
};
