import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../firebase'; // adaptat la setupul tău
import { auth, db } from '../services/firebase'; // doar dacă încă folosești Firebase
import { collection, getDocs, query, where } from 'firebase/firestore'; // dacă păstrezi istoricul Firebase
import Sidebar from './Sidebar';
import '../styles/Home.css';

function Home() {
  const { currentUser } = useAuth();
  const navigate = useNavigate();
  const [isExpanded, setIsExpanded] = useState(false);
  const [selectedPage, setSelectedPage] = useState('welcome');
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    const fetchUserSearches = async () => {
      try {
        if (currentUser) {
          const q = query(collection(db, 'searches'), where('uid', '==', currentUser.uid));
          const querySnapshot = await getDocs(q);
          const searchesFromDb = querySnapshot.docs.map(doc => doc.data().query);
          setSearchResults(searchesFromDb);
        }
      } catch (error) {
        console.error('Eroare la încărcarea căutărilor din Firebase:', error);
      }
    };

    fetchUserSearches();
  }, [currentUser]);

  const handleLogout = async () => {
    await auth.signOut();
    navigate('/');
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchQuery.trim()) return;

    try {
      await fetch('http://localhost:5000/api/cautari', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ uid: currentUser.uid, termen: searchQuery })
      });

      navigate(`/rezultate?termen=${encodeURIComponent(searchQuery)}`);
    } catch (err) {
      console.error('Eroare trimitere căutare la backend:', err);
    }
  };

  return (
    <div className="home-container">
      <Sidebar handleLogout={handleLogout} />
      <main className="home-content">
        <div className="home-welcome">
          <h1>Bun venit în aplicația de comparare a prețurilor</h1>
          <form onSubmit={handleSearch} className="search-form">
            <input
              type="text"
              placeholder="Caută un produs..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <button type="submit">Caută</button>
          </form>
        </div>
      </main>
    </div>
  );
}

export default Home;
