import React, { useEffect, useState } from 'react';
import { getSearchHistory } from '../services/api';
import { auth } from '../services/firebase';

function HistoryPage() {
  const [istoric, setIstoric] = useState([]);

  useEffect(() => {
    const fetchHistory = async () => {
      const user = auth.currentUser;
      if (user) {
        const results = await getSearchHistory(user.uid);
        setIstoric(results);
      }
    };

    fetchHistory();
  }, []);

  return (
    <div style={{ padding: "2rem" }}>
      <h2>Istoric căutări</h2>
      {istoric.length === 0 ? (
        <p>Nu ai încă nicio căutare salvată.</p>
      ) : (
        <ul>
          {istoric.map(entry => (
            <li key={entry.id}>
              <strong>{entry.term}</strong> – {entry.timestamp?.toDate().toLocaleString()}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default HistoryPage;
