import React, { useEffect, useState } from 'react';
import { useAuth } from '../firebase'; // adaptează după setupul tău

export default function HistoryPage() {
  const [istoric, setIstoric] = useState([]);
  const { currentUser } = useAuth();

  useEffect(() => {
    if (currentUser) {
      fetch(`http://localhost:5000/api/cautari?uid=${currentUser.uid}`)
        .then(res => res.json())
        .then(data => setIstoric(data))
        .catch(err => console.error('Eroare istoric:', err));
    }
  }, [currentUser]);

  return (
    <div>
      <h2>Istoric căutări</h2>
      <ul>
        {istoric.map((item, index) => (
          <li key={index}>
            {item.termen} – {item.data}
          </li>
        ))}
      </ul>
    </div>
  );
}
