import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import { cautaProduse } from '../services/api';

function Rezultate() {
  const [produse, setProduse] = useState([]);
  const [filtruPret, setFiltruPret] = useState(''); // "asc" sau "desc"
  const [filtruNume, setFiltruNume] = useState('');
  const [loading, setLoading] = useState(true);

  const location = useLocation();
  const termen = new URLSearchParams(location.search).get('termen');

  useEffect(() => {
    const fetchResults = async () => {
      try {
        const data = await cautaProduse(termen);
        setProduse(data);
      } catch (err) {
        console.error("Eroare la încărcarea rezultatelor:", err);
      } finally {
        setLoading(false);
      }
    };

    if (termen) {
      fetchResults();
    }
  }, [termen]);

  // Aplica filtre local
  const produseFiltrate = [...produse]
    .filter(p =>
      p.denumire.toLowerCase().includes(filtruNume.toLowerCase())
    )
    .sort((a, b) => {
      if (filtruPret === 'asc') return a.pret - b.pret;
      if (filtruPret === 'desc') return b.pret - a.pret;
      return 0;
    });

  if (loading) {
    return <div style={{ padding: '2rem' }}>Se încarcă...</div>;
  }

  return (
    <div style={{ padding: "2rem" }}>
      <h2>Rezultate pentru: <em>{termen}</em></h2>

      {/* Bara de filtre */}
      <div style={{ marginBottom: '1rem' }}>
        <input
          type="text"
          placeholder="Filtrează după nume"
          value={filtruNume}
          onChange={(e) => setFiltruNume(e.target.value)}
          style={{ marginRight: '1rem' }}
        />
        <select
          value={filtruPret}
          onChange={(e) => setFiltruPret(e.target.value)}
        >
          <option value="">Filtru preț</option>
          <option value="asc">Preț crescător</option>
          <option value="desc">Preț descrescător</option>
        </select>
      </div>

      {produseFiltrate.length === 0 ? (
        <p>Nu s-au găsit produse.</p>
      ) : (
        <ul>
          {produseFiltrate.map((produs, index) => (
            <li key={index}>
              <strong>{produs.denumire}</strong> – {produs.pret} lei<br />
              Link: <a href={produs.link_cumparare} target="_blank" rel="noopener noreferrer">{produs.link_cumparare || 'N/A'}</a><br />
              ID Magazin: {produs.id_magazin || 'N/A'}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default Rezultate;
