// src/services/api.js

import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL;

export const cautaProduse = async (termen) => {
  const res = await axios.get(`http://localhost:5000/api/products/search/${encodeURIComponent(termen)}`);
  return res.data;
};

export async function getAllProduse() {
  const res = await axios.get('http://localhost:5000/api/products');
  return res.data;
}

export async function incarcaToateProdusele() {
  const res = await fetch(`${API_URL}/products`);
  if (!res.ok) throw new Error("Eroare la încărcare produse");
  return await res.json();
}
