import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import { cautaProduse } from '../services/api';

function Rezultate() {
  const [produse, setProduse] = useState([]);
  const [filtru, setFiltru] = useState('toate');
  const [loading, setLoading] = useState(true);

  const location = useLocation();
  const termen = new URLSearchParams(location.search).get('termen');

  useEffect(() => {
    const fetchResults = async () => {
      try {
        const data = await cautaProduse(termen);
        setProduse(data);
      } catch (err) {
        console.error("Eroare la încărcarea rezultatelor:", err);
      } finally {
        setLoading(false);
      }
    };

    if (termen) {
      fetchResults();
    }
  }, [termen]);

  const filtreazaProduse = () => {
    if (filtru === 'toate') return produse;
    if (filtru === 'sub5') return produse.filter(p => p.pret < 5);
    if (filtru === 'peste5') return produse.filter(p => p.pret >= 5);
    return produse;
  };

  const produseAfisate = filtreazaProduse();

  if (loading) return <div style={{ padding: '2rem' }}>Se încarcă...</div>;

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Rezultate pentru: <em>{termen}</em></h2>

      {/* Bara de filtre */}
      <div style={{ marginBottom: '1rem' }}>
        <button onClick={() => setFiltru('toate')}>Toate</button>{' '}
        <button onClick={() => setFiltru('sub5')}>Sub 5 lei</button>{' '}
        <button onClick={() => setFiltru('peste5')}>Peste 5 lei</button>
      </div>

      {produseAfisate.length === 0 ? (
        <p>Nu s-au găsit produse.</p>
      ) : (
        <ul>
          {produseAfisate.map((produs) => (
            <li key={produs.id || produs.denumire}>
              <strong>{produs.denumire}</strong> – {produs.pret} lei<br />
              Link: <a href={produs.link_cumparare} target="_blank" rel="noopener noreferrer">{produs.link_cumparare}</a><br />
              ID Magazin: {produs.id_magazin}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default Rezultate;
