import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { cautaProduse } from '../services/api';
import Paginator from './Paginator';
import '../styles/ProductsList.css';

function useQuery() {
  return new URLSearchParams(useLocation().search);
}

const Rezultate = () => {
  const navigate = useNavigate();
  const query = useQuery();
  const termen = query.get('term') || '';
  
  const [toateProdusele, setToateProdusele] = useState([]);
  const [produse, setProduse] = useState([]);
  const [paginaCurenta, setPaginaCurenta] = useState(1);
  const [dimensiunePagina, setDimensiunePagina] = useState(10);
  const [sort, setSort] = useState('denumire_asc');

  const fetchResults = async () => {
    try {
      const rezultate = await cautaProduse(termen);
      setToateProdusele(rezultate);
    } catch (err) {
      console.error('Eroare la încărcarea rezultatelor:', err);
    }
  };

  useEffect(() => {
    fetchResults();
  }, [termen]);

  useEffect(() => {
    let filtrate = [...toateProdusele];

    if (sort === 'denumire_asc') {
      filtrate.sort((a, b) => a.denumire.localeCompare(b.denumire));
    } else if (sort === 'denumire_desc') {
      filtrate.sort((a, b) => b.denumire.localeCompare(a.denumire));
    }

    const start = (paginaCurenta - 1) * dimensiunePagina;
    const end = start + dimensiunePagina;
    setProduse(filtrate.slice(start, end));
  }, [toateProdusele, paginaCurenta, dimensiunePagina, sort]);

  return (
    <div className="products-list">
      <h1>Rezultate pentru: <em>{termen}</em></h1>

      <div style={{ marginBottom: '1rem' }}>
        <label>Sortează după: </label>
        <select value={sort} onChange={(e) => setSort(e.target.value)}>
          <option value="denumire_asc">Denumire (A-Z)</option>
          <option value="denumire_desc">Denumire (Z-A)</option>
        </select>
      </div>

      <table>
        <thead>
          <tr>
            <th>Denumire</th>
            <th>Preț</th>
          </tr>
        </thead>
        <tbody>
          {produse.map((produs) => (
            <tr key={produs.id}>
              <td>{produs.denumire}</td>
              <td>{produs.pret} lei</td>
            </tr>
          ))}
        </tbody>
      </table>

      <Paginator
        onPageChange={(pagina) => setPaginaCurenta(pagina)}
        onPageSizeChange={(size) => {
          setDimensiunePagina(size);
          setPaginaCurenta(1);
        }}
        totalRecords={toateProdusele.length}
      />

      <div className="footer">
        <button onClick={() => navigate('/home')}>Înapoi</button>
      </div>
    </div>
  );
};

export default Rezultate;
